import { Button, createMuiTheme, Grid, Paper, Typography, withStyles } from '@material-ui/core';
import { ThemeProvider } from '@material-ui/styles';
import React from 'react';
import { Link } from 'react-router-dom';
import backgroundPic from '../../../Logo/healthy-diet.jpg'
import weightLoss from '../../../Logo/weight-loss.jpg'
import weightGain from '../../../Logo/weight-gain.png'
import sports from '../../../Logo/sports.jpg'
import healthy from '../../../Logo/healthy.jpg'


export default class HomeContainer extends React.Component {

     theme = createMuiTheme({
        typography: {
          fontFamily: [
            'Chilanka',
            'cursive',
          ].join(','),
        },});
     StyledButton = withStyles({
        root: {
          background: 'linear-gradient(45deg, #FE6B8B 5%, #FF8E53 100%)',
          borderRadius: 3,
          border: 0,
          color: 'white',
          height: 48,
          padding: '0 30px',
          boxShadow: '0 3px 5px 2px rgba(255, 105, 135, .3)',
        },
        label: {
          textTransform: 'capitalize',
        },
      })(Button);
    render(){
        
        return (
            <div style={{marginTop:'60px',width:'100%'}}>
            <div style={{ backgroundImage:`url(${backgroundPic})`,height:'80vh',backgroundRepeat:'no-repeat',backgroundSize: '100%',marginRight:'0px',paddingTop:'330px',
                        textAlign: 'center'}}>
            <Typography  variant="h5" style={{color:'orange'}} ><b>Koга исхраната е исправна, од медицината нема потреба</b></Typography>
            <Typography  variant="h5" style={{color:'orange'}}><b>Заедно креираме здрави навики</b></Typography>
            <ThemeProvider theme={this.theme}>
            <Link to='\contact' ><this.StyledButton style={{marginTop:'10px'}}><b>ПРИДРУЖИ СЕ</b></this.StyledButton></Link>
            </ThemeProvider>
            </div> 
            <Grid container spacing={2} style={{textAlign:'center'}}>
              
              <Grid item xs={3}>  {/*slabeenje*/}
              <Paper variant="outlined">
              <img src={weightLoss} style={{borderTopLeftRadius: '50%',borderTopRightRadius: '50%',borderBottomRightRadius: '50%',borderBottomLeftRadius: '50%'
              ,width:'200px',height:'200px'}} />
            </Paper>
              </Grid>
              <Grid item xs={3}> {/*zdebeluvanje*/}

              </Grid>
              <Grid item xs={3}> {/*sportski*/}
              <h1>TEST2132131231111</h1>

              </Grid>
              <Grid item xs={3}> {/*zdravstvo*/}
              <h1>321312321</h1>

              </Grid>
              </Grid>
            </div>
        )}
}
